import React, { useState } from "react";
import "./index.css";
import Accordion from "./cart/Accordion";
import ProductTile from "./cart/ProductTile";
import Header from "./cart/Header";

const App = () => {

  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState(null);
  const [cartCount, setCartCount] = useState(0);

  const handleCategorySelect = (categoryName) => {
    setSelectedCategory(
      categoryName === selectedCategory ? null : categoryName
    );
    setSelectedSubcategory(null); // Reset selected subcategory when changing category
  };

  const handleSubcategorySelect = (subcategoryName) => {
    setSelectedSubcategory(
      subcategoryName === selectedSubcategory ? null : subcategoryName
    );
  };

  const handleAddToCart = () => {
    setCartCount(cartCount + 1);
  };

  // Find the products based on the selected subcategory
  const productsToShow =
    selectedCategory &&
    categories
      .find((cat) => cat.name === selectedCategory)
      .subcategories.find((sub) => sub.name === selectedSubcategory)?.items;

  return (
    <div className="app">
      <Header cartCount={cartCount} />
      <div className="content">
        <Accordion
          categories={categories}
          selectedCategory={selectedCategory}
          selectedSubcategory={selectedSubcategory}
          onCategorySelect={handleCategorySelect}
          onSubcategorySelect={handleSubcategorySelect}
        />
        <div className="products">
          {!selectedCategory && (
            <div className="category-header">
              <h2>Please select a category</h2>
            </div>
          )}
          {selectedCategory && (
            <div className="category-header">
              <h2>{selectedCategory}</h2>
            </div>
          )}
          {productsToShow &&
            productsToShow.map((product) => (
              <ProductTile
                key={product.id}
                product={product}
                onAddToCart={handleAddToCart}
              />
            ))}
        </div>
      </div>
    </div>
  );
};

export default App;
