import React, { useState } from "react";

const Accordion = ({ categories, selectedSubcategory, onSubcategorySelect }) => {
  const [expandedCategory, setExpandedCategory] = useState(null);

  const handleCategorySelect = (categoryName) => {
    setExpandedCategory(prevCategory =>
      prevCategory === categoryName ? null : categoryName
    );
  };

  return (
    <div className="accordion">
      {categories.map((category) => (
        <div key={category.name} className="accordion-item">
          <button
            className={`accordion-category ${
              expandedCategory === category.name ? "expanded" : ""
            }`}
            onClick={() => handleCategorySelect(category.name)}
          >
            {expandedCategory === category.name ? "−" : "+"} {category.name}
          </button>
          {expandedCategory === category.name && (
            <div className="accordion-content">
              {category.subcategories.map((subcategory) => (
                <div
                  key={subcategory.name}
                  className={`accordion-subcategory ${
                    selectedSubcategory === subcategory.name ? "highlight" : ""
                  }`}
                  onClick={() => onSubcategorySelect(subcategory.name)}
                >
                  {subcategory.name}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Accordion;
