import React, { useState } from "react";
import "./index.css";
import Accordion from "./cart/Accordion";
import ProductTile from "./cart/ProductTile";
import Header from "./cart/Header";

const App = () => {
  const categories = [
    {
      name: "Electronics",
      subcategories: [
        {
          name: "Computers",
          items: [
            { id: 1, title: "Mac", description: "Apple MacBook" },
            { id: 2, title: "Dell", description: "Dell XPS" },
            { id: 3, title: "HP", description: "HP Pavilion" },
            { id: 4, title: "Notebook", description: "Notebook" },
            { id: 5, title: "Lenevo Thinkpad", description: "Lenevo" },
          ],
        },
        {
          name: "Phones",
          items: [
            { id: 5, title: "iPhone", description: "Apple iPhone" },
            { id: 6, title: "Samsung", description: "Samsung Galaxy" },
          ],
        },
        {
          name: "Televisions",
          items: [
            { id: 7, title: "Sony", description: "Sony Bravia" },
            { id: 8, title: "LG", description: "LG OLED" },
          ],
        },
      ],
    },
    {
      name: "Books",
      subcategories: [
        {
          name: "Fiction",
          items: [
            {
              id: 9,
              title: "1984",
              description: "George Orwell's dystopian novel",
            },
            {
              id: 10,
              title: "The Great Gatsby",
              description: "F. Scott Fitzgerald's classic novel",
            },
          ],
        },
        {
          name: "Non-Fiction",
          items: [
            {
              id: 11,
              title: "Sapiens",
              description: "Yuval Noah Harari's exploration of human history",
            },
            {
              id: 12,
              title: "Educated",
              description: "Tara Westover's memoir",
            },
          ],
        },
        {
          name: "Science",
          items: [
            {
              id: 13,
              title: "A Brief History of Time",
              description: "Stephen Hawking's scientific book",
            },
            {
              id: 14,
              title: "The Gene",
              description: "Siddhartha Mukherjee's exploration of genetics",
            },
          ],
        },
      ],
    },
    {
      name: "Clothing",
      subcategories: [
        {
          name: "Men's Wear",
          items: [
            { id: 15, title: "T-Shirt", description: "Casual men's t-shirt" },
            { id: 16, title: "Jeans", description: "Denim jeans for men" },
          ],
        },
        {
          name: "Women's Wear",
          items: [
            { id: 17, title: "Dress", description: "Elegant women's dress" },
            { id: 18, title: "Skirt", description: "Stylish women's skirt" },
          ],
        },
        {
          name: "Accessories",
          items: [
            { id: 19, title: "Watch", description: "Elegant wristwatch" },
            { id: 20, title: "Hat", description: "Fashionable hat" },
          ],
        },
      ],
    },
  ];

  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState(null);
  const [cartCount, setCartCount] = useState(0);

  const handleCategorySelect = (categoryName) => {
    setSelectedCategory(
      categoryName === selectedCategory ? null : categoryName
    );
    setSelectedSubcategory(null); // Reset selected subcategory when changing category
  };

  const handleSubcategorySelect = (subcategoryName) => {
    setSelectedSubcategory(
      subcategoryName === selectedSubcategory ? null : subcategoryName
    );
  };

  const handleAddToCart = () => {
    setCartCount(cartCount + 1);
  };

  // Find the products based on the selected subcategory
  const productsToShow =
    selectedCategory &&
    categories
      .find((cat) => cat.name === selectedCategory)
      .subcategories.find((sub) => sub.name === selectedSubcategory)?.items;

  return (
    <div className="app">
      <Header cartCount={cartCount} />
      <div className="content">
        <Accordion
          categories={categories}
          selectedCategory={selectedCategory}
          selectedSubcategory={selectedSubcategory}
          onCategorySelect={handleCategorySelect}
          onSubcategorySelect={handleSubcategorySelect}
        />
        <div className="products">
          {productsToShow &&
            productsToShow.map((product) => (
              <ProductTile
                key={product.id}
                product={product}
                onAddToCart={handleAddToCart}
              />
            ))}
        </div>
      </div>
    </div>
  );
};

export default App;
