import React, { useState } from "react";
import "./index.css";
import Accordion from "./cart/Accordion";
import ProductTile from "./cart/ProductTile";
import Header from "./cart/Header";

const App = () => {
  const categories = [
    {
      name: "Electronics",
      subcategories: [
        { id: 1, title: "Laptop", description: "High-performance laptop" },
        { id: 2, title: "Smartphone", description: "Latest smartphone" },
      ],
    },
    {
      name: "Books",
      subcategories: [
        { id: 3, title: "Book 1", description: "Interesting book" },
        { id: 4, title: "Book 2", description: "Another interesting book" },
      ],
    },
    {
      name: "Clothing",
      subcategories: [
        { id: 5, title: "Shirt", description: "Stylish shirt" },
        { id: 6, title: "Jeans", description: "Comfortable jeans" },
      ],
    },
  ];

  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [cartCount, setCartCount] = useState(0);

  const handleCategorySelect = (categoryName) => {
    setSelectedCategory(
      categoryName === selectedCategory ? null : categoryName
    );
    setSelectedProduct(null); // Reset selected product when changing category
  };

  const handleProductSelect = (product) => {
    setSelectedProduct(product);
  };

  const handleAddToCart = () => {
    setCartCount(cartCount + 1);
  };

  return (
    <div className="app">
      <Header cartCount={cartCount} />
      <div className="content">
        <Accordion
          categories={categories}
          selectedCategory={selectedCategory}
          onCategorySelect={handleCategorySelect}
          onProductSelect={handleProductSelect}
        />
        <div className="products">
          {selectedProduct && (
            <ProductTile
              product={selectedProduct}
              onAddToCart={handleAddToCart}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
