import React, { useState } from "react";

const Accordion = ({
  categories,
  selectedSubcategory,
  onCategorySelect,
  onSubcategorySelect,
}) => {
  // State to track expanded categories
  const [expandedCategories, setExpandedCategories] = useState({});

  const handleCategoryToggle = (categoryName) => {
    setExpandedCategories((prev) => ({
      ...prev,
      [categoryName]: !prev[categoryName], // Toggle the expansion state
    }));
  };

  return (
    <div className="accordion">
      {categories.map((category) => (
        <div key={category.name} className="accordion-item">
          <button
            className={`accordion-category ${
              expandedCategories[category.name] ? "expanded" : ""
            }`}
            onClick={() => {
              handleCategoryToggle(category.name);
              onCategorySelect(category.name);
            }}
          >
            {expandedCategories[category.name] ? "−" : "+"} {category.name}
          </button>
          {expandedCategories[category.name] && (
            <div className="accordion-content">
              {category.subcategories.map((subcategory) => (
                <div
                  key={subcategory.name}
                  className={`accordion-subcategory ${
                    selectedSubcategory === subcategory.name ? "highlight" : ""
                  }`}
                  onClick={() => onSubcategorySelect(subcategory.name)}
                >
                  {subcategory.name}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Accordion;
