import React, { useState } from "react";

const Accordion = ({
  categories,
  products,
  selectedCategory,
  onCategorySelect,
  selectedProduct,
  onProductSelect,
}) => {
  const [expandedCategory, setExpandedCategory] = useState(null);

  const handleCategoryClick = (category) => {
    if (expandedCategory === category) {
      setExpandedCategory(null); // Collapse if the category is already expanded
    } else {
      setExpandedCategory(category); // Expand the selected category
    }
    onCategorySelect(category);
  };

  const handleProductClick = (product) => {
    onProductSelect(product);
  };

  return (
    <div className="accordion">
      {categories.map((category, index) => (
        <div key={index} className="accordion-item">
          <button
            className={`accordion-category ${
              expandedCategory === category ? "expanded" : ""
            }`}
            onClick={() => handleCategoryClick(category)}
          >
            {category}
          </button>
          {expandedCategory === category && (
            <div className="accordion-content">
              {products[category].map((product) => (
                <div
                  key={product.id}
                  className={`accordion-product ${
                    selectedProduct?.id === product.id ? "selected" : ""
                  }`}
                  onClick={() => handleProductClick(product)}
                >
                  {product.title}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Accordion;
