import React, { useState } from "react";
import "./index.css";
import Accordion from "./cart/Accordion";
import ProductTile from "./cart/ProductTile";
import Header from "./cart/Header";

const App = () => {
  const categories = [
    {
      name: "Electronics",
      subcategories: ["Computers", "Phones", "Televisions"],
    },
    { name: "Books" },
    { name: "Clothing" },
  ];

  const products = {
    Computers: [
      { id: 1, title: "Mac", description: "Apple MacBook Pro" },
      { id: 2, title: "Dell", description: "Dell XPS 13" },
    ],
    Phones: [
      { id: 3, title: "iPhone", description: "Apple iPhone 12" },
      { id: 4, title: "Samsung", description: "Samsung Galaxy S21" },
    ],
    Televisions: [
      { id: 5, title: "LG", description: "LG OLED TV" },
      { id: 6, title: "Sony", description: "Sony Bravia" },
    ],
    Books: [
      { id: 7, title: "Book 1", description: "Interesting book" },
      { id: 8, title: "Book 2", description: "Another interesting book" },
    ],
    Clothing: [
      { id: 9, title: "Shirt", description: "Stylish shirt" },
      { id: 10, title: "Jeans", description: "Comfortable jeans" },
    ],
  };

  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState(null);
  const [cartCount, setCartCount] = useState(0);

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setSelectedSubcategory(null); // Reset subcategory when a new category is selected
  };

  const handleSubcategorySelect = (subcategory) => {
    setSelectedSubcategory(subcategory);
  };

  const handleAddToCart = () => {
    setCartCount(cartCount + 1);
  };

  return (
    <div className="app">
      <Header cartCount={cartCount} />
      <div className="content">
        <Accordion
          categories={categories}
          selectedCategory={selectedCategory}
          selectedSubcategory={selectedSubcategory}
          onCategorySelect={handleCategorySelect}
          onSubcategorySelect={handleSubcategorySelect}
        />
        <div className="products">
          {selectedSubcategory && products[selectedSubcategory] ? (
            products[selectedSubcategory].map((product) => (
              <ProductTile
                key={product.id}
                product={product}
                onAddToCart={handleAddToCart}
              />
            ))
          ) : (
            <p>Select a subcategory to view products.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
