import React, { useState } from "react";
import "./index.css";
import Accordion from "./cart/Accordion";

const App = () => {
  const categories = ["Electronics", "Books", "Clothing"];
  const products = {
    Electronics: [
      { id: 1, title: "Laptop", description: "High-performance laptop" },
      { id: 2, title: "Smartphone", description: "Latest smartphone" },
    ],
    Books: [
      { id: 3, title: "Book 1", description: "Interesting book" },
      { id: 4, title: "Book 2", description: "Another interesting book" },
    ],
    Clothing: [
      { id: 5, title: "Shirt", description: "Stylish shirt" },
      { id: 6, title: "Jeans", description: "Comfortable jeans" },
    ],
  };

  const [selectedCategory, setSelectedCategory] = useState(categories[0]);
  const [cartCount, setCartCount] = useState(0);

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
  };

  const handleAddToCart = () => {
    setCartCount(cartCount + 1);
  };

  return (
    <div className="app">
      <Header cartCount={cartCount} />
      <div className="content">
        <Accordion
          categories={categories}
          onCategorySelect={handleCategorySelect}
        />
        <div className="products">
          {products[selectedCategory].map((product) => (
            <ProductTile
              key={product.id}
              product={product}
              onAddToCart={handleAddToCart}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
