import React, { useState } from "react";

const Accordion = ({ categories }) => {
  const [expandedCategory, setExpandedCategory] = useState(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState(null);

  const handleCategoryClick = (category) => {
    setExpandedCategory(expandedCategory === category ? null : category);
  };

  const handleSubcategoryClick = (subcategory) => {
    setSelectedSubcategory(subcategory);
  };

  return (
    <div className="accordion">
      {categories.map((category, index) => (
        <div key={index} className="accordion-item">
          <button
            className={`accordion-category ${
              expandedCategory === category.name ? "expanded" : ""
            }`}
            onClick={() => handleCategoryClick(category.name)}
          >
            {expandedCategory === category.name ? "−" : "+"} {category.name}
          </button>
          {expandedCategory === category.name && (
            <div className="accordion-content">
              {category.subcategories.map((subcategory, subIndex) => (
                <div
                  key={subIndex}
                  className={`accordion-subcategory ${
                    selectedSubcategory === subcategory ? "selected" : ""
                  }`}
                  onClick={() => handleSubcategoryClick(subcategory)}
                >
                  {subcategory}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Accordion;
