import React from "react";

const Accordion = ({
  categories,
  selectedCategory,
  selectedSubcategory,
  onCategorySelect,
  onSubcategorySelect,
}) => {
  // Function to check if a category has subcategories
  const hasSubcategories = (categoryName) => {
    const category = categories.find((cat) => cat.name === categoryName);
    return category && category.subcategories.length > 0;
  };

  return (
    <div className="accordion">
      {categories.map((category, index) => (
        <React.Fragment key={index}>
          <div className="accordion-item">
            <button
              className={`accordion-category ${
                selectedCategory === category.name ? "expanded" : ""
              }`}
              onClick={() => onCategorySelect(category.name)}
            >
              <span className="icon-box">
                {selectedCategory === category.name ? "-" : "+"}
              </span>
              <span className="accordion-label">{category.name}</span>
            </button>
            {selectedCategory === category.name && (
              <div className="accordion-content">
                {category.subcategories.length > 0 ? (
                  category.subcategories.map((subcategory) => (
                    <div
                      key={subcategory.name}
                      className={`accordion-subcategory ${
                        selectedSubcategory === subcategory.name
                          ? "highlight"
                          : ""
                      }`}
                      onClick={() => onSubcategorySelect(subcategory.name)}
                    >
                      {subcategory.name}
                    </div>
                  ))
                ) : (
                  <div className="default-message">
                    No subcategories available for {category.name}.
                  </div>
                )}
              </div>
            )}
          </div>
        </React.Fragment>
      ))}
    </div>
  );
};

export default Accordion;
