import React, { useState } from "react";
import "./index.css";
import Accordion from "./cart/Accordion";
import ProductTile from "./cart/ProductTile";
import Header from "./cart/Header";

const App = () => {
  const categories = [
    {
      name: "Electronics",
      subcategories: [
        {
          name: "Computers",
          items: [
            { id: 1, title: "Mac", description: "Apple MacBook" },
            { id: 2, title: "Dell", description: "Dell XPS" },
          ],
        },
        {
          name: "Phones",
          items: [
            { id: 3, title: "iPhone", description: "Apple iPhone" },
            { id: 4, title: "Samsung", description: "Samsung Galaxy" },
          ],
        },
        {
          name: "Televisions",
          items: [
            { id: 5, title: "Sony", description: "Sony Bravia" },
            { id: 6, title: "LG", description: "LG OLED" },
          ],
        },
      ],
    },
    { name: "Books", subcategories: [] },
    { name: "Clothing", subcategories: [] },
  ];

  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState(null);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [cartCount, setCartCount] = useState(0);

  const handleCategorySelect = (categoryName) => {
    setSelectedCategory(
      categoryName === selectedCategory ? null : categoryName
    );
    setSelectedSubcategory(null); // Reset selected subcategory when changing category
    setSelectedProduct(null); // Reset selected product when changing category
  };

  const handleSubcategorySelect = (subcategoryName) => {
    setSelectedSubcategory(
      subcategoryName === selectedSubcategory ? null : subcategoryName
    );
    setSelectedProduct(null); // Reset selected product when changing subcategory
  };

  const handleProductSelect = (product) => {
    setSelectedProduct(product);
  };

  const handleAddToCart = () => {
    setCartCount(cartCount + 1);
  };

  return (
    <div className="app">
      <Header cartCount={cartCount} />
      <div className="content">
        <Accordion
          categories={categories}
          selectedCategory={selectedCategory}
          selectedSubcategory={selectedSubcategory}
          onCategorySelect={handleCategorySelect}
          onSubcategorySelect={handleSubcategorySelect}
          onProductSelect={handleProductSelect}
        />
        <div className="products">
          {selectedProduct && (
            <ProductTile
              product={selectedProduct}
              onAddToCart={handleAddToCart}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
