import React from "react";

const Accordion = ({
  categories,
  selectedCategory,
  selectedSubcategory,
  onCategorySelect,
  onSubcategorySelect,
  onProductSelect,
}) => {
  return (
    <div className="accordion">
      {categories.map((category, index) => (
        <div key={index} className="accordion-item">
          <button
            className={`accordion-category ${
              selectedCategory === category.name ? "expanded" : ""
            }`}
            onClick={() => onCategorySelect(category.name)}
          >
            {selectedCategory === category.name ? "−" : "+"} {category.name}
          </button>
          {selectedCategory === category.name && (
            <div className="accordion-content">
              {category.subcategories.map((subcategory) => (
                <div key={subcategory.name}>
                  <div
                    className={`accordion-subcategory ${
                      selectedSubcategory === subcategory.name
                        ? "highlight"
                        : ""
                    }`}
                    onClick={() => onSubcategorySelect(subcategory.name)}
                  >
                    {subcategory.name}
                  </div>
                  {selectedSubcategory === subcategory.name && (
                    <div className="accordion-items">
                      {subcategory.items.map((product) => (
                        <div
                          key={product.id}
                          className="accordion-item-detail"
                          onClick={() => onProductSelect(product)}
                        >
                          {product.title}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Accordion;
