import React from "react";

const Accordion = ({
  categories,
  selectedCategory,
  selectedSubcategory,
  onCategorySelect,
  onSubcategorySelect,
}) => {
  return (
    <div className="accordion">
      {categories.map((category, index) => (
        <>
          {" "}
          <div key={index} className="accordion-item">
            <button
              className={`accordion-category ${
                selectedCategory === category.name ? "expanded" : ""
              }`}
              onClick={() => onCategorySelect(category.name)}
            >
              <span className="icon-box">
                {selectedCategory === category.name ? "-" : "+"}
              </span>
              <span className="accordion-label">{category.name}</span>
              {/* {selectedCategory === category.name ? "âˆ’" : "+"} {category.name} */}
            </button>
            {selectedCategory === category.name && (
              <div className="accordion-content">
                {category.subcategories.map((subcategory) => (
                  <div
                    key={subcategory.name}
                    className={`accordion-subcategory ${
                      selectedSubcategory === subcategory.name
                        ? "highlight"
                        : ""
                    }`}
                    onClick={() => onSubcategorySelect(subcategory.name)}
                  >
                    {subcategory.name}
                  </div>
                ))}
              </div>
            )}
          </div>
          <div className="accordion-item-border"></div>
        </>
      ))}
    </div>
  );
};

export default Accordion;
